function [imagesRootDir,dirSep] = SimDefineImagePath(imageFolderName)% [imagesRootDir,dirSep] = SimDefineImagePath([imageFolderName])%% Define the path to the folder containing the default hyperspectral images.%% 1/13/99   dhb  Stand alone.% 1/18/99   dhb  Call through PsychToolbox FindFolder, so that%                you shouldn't have to configure this for your system.%           dhb  Made it a function, allow passing a folder name.% Find the folder by name.  It must be on MATLAB's path.if (nargin < 1)	imageFolderName = 'SimImages';endimagesRootDir = FindFolder(imageFolderName);% Check that it worked.if isempty(imagesRootDir)	error(['Can''t find any ''' imageFolderName ''' folder in the MATLAB path.']);endif size(imagesRootDir,1)>1	for i=1:size(imagesRootDir,1)		disp(['DUPLICATE: ''' deblank(imagesRootDir(i,:)) '''']);	end	error(['Found more than one ''' imageFolderName ''' folder in the MATLAB path.']);endimagesRootDir = deblank(imagesRootDir);% Define separator characterdirSep = '/';if strcmp(computer,'MAC2')	dirSep = ':';elseif strcmp(computer,'PCWIN')	dirSep = '\';end