function [outImage] = SimDoNotInterp(inImage)% [outImage] = SimDoNotInterp(inImage)%% Does not interpolate the image, but changes its flag.% Created for consistency reasons when using non mosaiced images.%% 05/17/00  pxl  Wrote it.if ( nargin <1 )  error(['SimDoNotInterp(inImage)']);endif (isstruct(inImage))  outImage = inImage;else  outImage = SimReadImage(inImage); endif (~isstruct(inImage.cameraFile))	camera = SimReadCamera(inImage.cameraFile);else	camera = inImage.cameraFile;end% Only act if image is mosaiced.if (camera.spatialLayout.dims(1)*camera.spatialLayout.dims(2) ~= 1 & ...    inImage.imageType == 'sensor' & inImage.mosaiced == 1 )  % Clear mosaiced flag  outImage.mosaiced = 0;  outImage.bits = 8;  outImage.unit = 'Disk';  % Image was not mosaiced, just pass on by.else  outImage = inImage;end