function [error] = BCCFindXChromPostFun(uv,y,colorPriors,sensors,loss)% [error] = BCCFindXChromPostFun(uv,y,colorPriors,sensors,loss)%% Error function for the chromaticity posterior search procedure.%% 9/2/02  dhb  Wrote it.% 8/15/03 dhb  Convert to uv rather than xy.% 8/19/03 dhb  New optimization routines, don't return g.% 3/29/04 dhb  Pass lumFactor, nEvals as part of loss.% Get some sizesN_x = 1;[nil,N_beta] = size(y);% Compute the expected lossprob = BCCEvaluateChromPosterior(uv,y,loss.lumFactor,loss.nEvals,colorPriors,sensors,loss);if (isnan(prob))	error = 2000;	return;elseif (prob == 0)	error = 2000;else	error = -log(prob);end