function [linear] = BCCPolarToLinearCC(cal,pol)% [linear] = BCCPolarToLinearCC(cal,pol)%% Converts from linear polar coordinates to linear% rectangular coordinates.%% Polar coordinates are defined as radius, azimuth, and elevation.%% 4/11/96  dhb  Wrote it.% 4/30/96  dhb  Fixed stupid bug, was calling LinearToPolar.% 11/08/98 pxl  Changed names of functions.% 4/21/99  dhb  Modified to handle case where passed dimension > 3.% 2/10/07  dhb  Update to call PolarToSensor.% Copyright (c) 1999 David Brainard and Philippe Longere.   All rights reserved.% Control whether we search in linear or polar coordinatesglobal POLARSEARCHif (POLARSEARCH == 0)	if (nargin == 1)		linear = cal;	else		linear = pol;	endelse	if (nargin == 1)		if (length(cal) > 3)			linear = [PolarToSensor(cal(1:3)) ; cal(4:end)];		else			linear = PolarToSensor(cal);		end	else		if (length(pol) > 3)			linear = [PolarToSensor(cal,pol(1:3)) ; pol(4:end)]		else			linear = PolarToSensor(cal,pol);		end	endend