function pisur = SimConstructPisur(M_s)%% We choose the unit vectors and all possible% unweighted sums.%% 12/31/98  dhb  Change name.% Copyright (c) 1999 David Brainard and Philippe Longere.   All rights reserved.% Compute column dimension for pisurindex = 1;for (i = 1:M_s);  index = index+1;endfor (i = 1:M_s)  for (j = i+1:M_s)    index = index+1;  endend	% Allocate pisur and fill itN_pisur = index-1;pisur = zeros(M_s,N_pisur);index = 1;for (i = 1:M_s);  pisur(i,index) = 1;  index = index+1;endfor (i = 1:M_s)  for (j = i+1:M_s)    pisur(i,index) = 1;    pisur(j,index) = 1;    index = index+1;  endend