function [] = SimDifferenceTif(fileIn1,fileIn2)% [] = SimDifferenceTif(fileIn1,fileIn2)%% Computes number of different elements in two% tiff images, prints out this number to the% command window.%% Uses dimensions of image1 in the difference,% so image2 must be at least as big as image1.%% 10/28/98  dhb  Added comments.% Copyright (c) 1999 David Brainard and Philippe Longere.   All rights reserved.% Check usageif ( nargin < 2)  error(['SimDifferenceTif(fileIn1,fileIn2)']);end% Read two diff filesimage1 = double(imread(fileIn1,'tif'));image2 = double(imread(fileIn2,'tif'));image3 = image1-image2(1:size(image1,1),1:size(image1,2),:);fprintf(1,'%d difs. elts. out of %d\n',size(find(image3),1),size(image3,1)*size(image3,2)*3);