function [] = SimSquaredDiff(SimfileIn1,SimfileIn2,SimfileOut)% [] = SimSquaredDiff(SimfileIn1,SimfileIn2,SimfileOut)%% Compute the squared difference between two images and write% it out.%% The routine assumes that the two input images are compatible,% it doesn't explicitly check.%% 10/28/98  dhb   Changed name.%           dhb   Speed up by using .^2 rather than multiply.% Check usageif ( nargin != 3 )  error(['SimSquaredDiff(SimfileIn1,SimfileIn2,SimfileOut)']);end% Read the input imagesSimimage1 = SimReadImage(SimfileIn1); Simimage2 = SimReadImage(SimfileIn2);% Propogate parametersSimimage=Simimage2;% Compute squared image.Simimage.images =(Simimage1.images-Simimage2.images).^2;% I don't quite understand what is happening here,% in terms of the name override.Simimage.imageName = SimfileOut(1:length(SimfileOut)-4);Simimage.imageName = [Simimage.imageName,'tif'];SimWriteImage(SimfileOut,Simimage);