function missing = SimCheckRequiredFields(theStruct,theList)% missing = SimCheckRequiredFields(theStruct,theList)%% Check that structure contains each field in the list.% The first missing field is returned in the string% missing.  If nothing is missing, returned string is% empty.%% 1/19/99  dhb  Pulled it out of a higher level routine.% Copyright (c) 1999 David Brainard and Philippe Longere.   All rights reserved.missing = '';fields = fieldnames(theStruct);for (l=1:size(theList,1))  if (isempty(strmatch(deblank(theList(l,:)),fields)))		missing = deblank(theList(l,:));		return  endend