function imagePathName = SimGetImagePath(imageFileName)% imagePathName = SimGetImagePath(imageFileName)%% Extract the directory holding a file from the% full filename.%% 1/18/99  dhb  Wrote it.% Copyright (c) 1999 David Brainard and Philippe Longere.   All rights reserved.% Get path separatorsif (isequal(computer,'PCWIN'))  pathSeparator='\';elseif (isequal(computer,'MAC2'))  pathSeparator=':';else  pathSeparator='/';endindex = find(imageFileName == pathSeparator);if (isempty(index))	imagePathName = '';else	imagePathName = imageFileName(1:(index(end)-1));end